site.headStyle:
body .editable-style.column-section.container-element {
    padding: 0;
    margin: 0;
}
/* section takes all width */
div.column-section {
  width: 100%;
}
div.column-section > a {
  width: 100%;
}
@media (min-width:481px) {
  .silex-editor div.column-section {
    /* leave space to drop, only in the editor */
    padding: 50px 0;
  }
  div.column-section > a {
    /* 3 elements per line */
    width: 33.333%;
  }
}
/* Navbar container */
.navbar {
  overflow: hidden;
  background-color: rgba(51, 51, 51, 0);
  font-family: Arial;
  z-index: 9;
}

/* Links inside the navbar */
.navbar a {
  float: left;
  font-size: 20px;
  color: white;
  text-align: center;
  padding: 14px 16px;
  font-weight: 600;
  font-family: "GlacialIndifferenceRegular";
  text-decoration: none;
  z-index: 9;
}

/* The dropdown container */
.dropdown {
  float: left;
  overflow: hidden;
    font-family: "GlacialIndifferenceRegular";
}

/* Dropdown button */
.dropdown .dropbtn {
  font-size: 21px;
  border: none;
  z-index: 9;
  outline: none;
  font-weight: 600;
  font-family: "GlacialIndifferenceRegular";
  color: white;
  padding: 14px 16px;
  background-color: inherit;

  margin: 0; /* Important for vertical align on mobile phones */
}

/* Add a red background color to navbar links on hover */
.navbar a:hover, .dropdown:hover .dropbtn {
  z-index: 9;
  transition: opacity .25s ease-in-out, color .25s ease-in;
  color:#F78536;
  opacity: 0.9;
}

/* Dropdown content (hidden by default) */
.dropdown-content {
  display: none;
  z-index: 9;
  position: absolute;
  background-color: #f9f9f9;
  min-width: 160px;
    font-family: "GlacialIndifferenceRegular";
  box-shadow: 0px 8px 16px 0px rgba(0,0,0,0.2);

}

/* Links inside the dropdown */
.dropdown-content a {
  float: none;
  color: black;
    font-family: "GlacialIndifferenceRegular";
  padding: 12px 16px;
  text-decoration: none;
  display: block;
  text-align: left;
}

/* Add a grey background color to dropdown links on hover */
.dropdown-content a:hover {
  background-color: #ddd;
}

/* Show the dropdown menu on hover */
.dropdown:hover .dropdown-content {
  display: block;
}

